00561         public void CheckSlideStart()
00562         {
00563             List<Note> adjusted = new();
00564             Note previousSlideStart = new Rest();
00565             foreach (Note x in this.Notes)
00566             {
00567                 if (x.NoteGenre.Equals("SLIDE"))
00568                 {
00569                     if (x.SlideStart != null && x.SlideStart.NoteType.Equals("NST") && !adjusted.Contains(x.SlideStart))
00570                     {
00571                         adjusted.Add(x.SlideStart);
00572                         previousSlideStart = new Tap(x.SlideStart);
00573                     }
00574                     else if (x.SlideStart == null)
00575                     {
00576                         Console.WriteLine("A SLIDE WITHOUT START WAS FOUND");
00577                         Console.WriteLine(x.Compose(1));
00578                         Console.WriteLine("This slide has start: " + (x.SlideStart == null));
00579                         throw new NullReferenceException("A SLIDE WITHOUT START WAS FOUND");
00580                     }
00581                 }
00582                 if (!x.NoteGenre.Equals("SLIDE_START"))
00583                 {
00584                     adjusted.Add(x);
00585                 }
00586             }
00587             this.Notes = new(adjusted);
00588         }
