00603         public static int CalculateLeastMeasure(List<Note> bar)
00604         {
00605             List<int> startTimeList = new List<int>();
00606             startTimeList.Add(0);
00607             foreach (Note x in bar)
00608             {
00609                 if (!startTimeList.Contains(x.Tick))
00610                 {
00611                     startTimeList.Add(x.Tick);
00612                 }
00613                 if (x.NoteType.Equals("BPM"))
00614                 {
00615                     //Console.WriteLine(x.Compose(0));
00616                 }
00617             }
00618             if (startTimeList[startTimeList.Count - 1] != 384)
00619             {
00620                 startTimeList.Add(384);
00621             }
00622             List<int> intervalCandidates = new List<int>();
00623             int minimalInterval = GCD(startTimeList[0], startTimeList[1]);
00624             for (int i = 1; i < startTimeList.Count; i++)
00625             {
00626                 minimalInterval = GCD(minimalInterval, startTimeList[i]);
00627             }
00628             return minimalInterval;
00629         }
