00669         public static int CalculateQuaver(int length)
00670         {
00671             int result = 0;
00672             const int definition = 384;
00673             int divisor = GCD(definition, length);
00674             int quaver = definition / divisor, beat = length / divisor;
00675             result = quaver;
00676             return result;
00677         }
