00687         public static List<Note> FinishBar(List<Note> bar, List<BPMChange> bpmChanges, int barNumber, int minimalQuaver)
00688         {
00689             List<Note> result = new List<Note>();
00690             bool writeRest = true;
00691             result.Add(bar[0]);
00692             for (int i = 0; i < 384; i += 384 / minimalQuaver)
00693             {
00694                 //Separate Touch and others to prevent ordering issue
00695                 Note bpm = new Rest();
00696                 List<Note> eachSet = new List<Note>();
00697                 List<Note> touchEachSet = new List<Note>();
00698                 //Set condition to write rest if appropriate
00699                 writeRest = true;
00700                 //Add Appropriate note into each set
00701                 Note lastNote = new Rest();
00702                 foreach (Note x in bar)
00703                 {
00704                     if ((x.Tick == i) && x.IsNote && !(x.NoteType.Equals("TTP") || x.NoteType.Equals("THO")))
00705                     {
00706                         if (x.NoteSpecificGenre.Equals("BPM"))
00707                         {
00708                             bpm = x;
00709                             //List<Note> tempSet = new List<Note>();
00710                             //tempSet.Add(x);
00711                             //tempSet.AddRange(eachSet);
00712                             //eachSet=tempSet;
00713                             //Console.WriteLine("A note was found at tick " + i + " of bar " + barNumber + ", it is "+x.NoteType);
00714                         }
00715                         else
00716                         {
00717                             eachSet.Add(x);
00718                             //Console.WriteLine("A note was found at tick " + i + " of bar " + barNumber + ", it is "+x.NoteType);
00719                             writeRest = false;
00720                             x.Prev = lastNote;
00721                             lastNote.Next = x;
00722                         }
00723                     }
00724                     else if ((x.Tick == i) && x.IsNote && (x.NoteType.Equals("TTP") || x.NoteType.Equals("THO")))
00725                     {
00726                         if (x.NoteSpecificGenre.Equals("BPM"))
00727                         {
00728                             bpm = x;
00729                             //Console.WriteLine("A note was found at tick " + i + " of bar " + barNumber + ", it is "+x.NoteType);
00730                         }
00731                         else
00732                         {
00733                             touchEachSet.Add(x);
00734                             //Console.WriteLine("A note was found at tick " + i + " of bar " + barNumber + ", it is "+x.NoteType);
00735                             writeRest = false;
00736                             x.Prev = lastNote;
00737                             lastNote.Next = x;
00738                         }
00739                     }
00740                     if (!x.NoteSpecificGenre.Equals("BPM"))
00741                     {
00742                         lastNote = x.NewInstance();
00743                     }
00744                 }
00745                 //Searching for BPM change. If find one, get into front.
00746                 if (bpm.BPM != 0)
00747                 {
00748                     List<Note> adjusted = new List<Note>();
00749                     adjusted.Add(bpm);
00750                     adjusted.AddRange(touchEachSet);
00751                     adjusted.AddRange(eachSet);
00752                     eachSet = adjusted;
00753                 }
00754                 else
00755                 {
00756                     List<Note> adjusted = new List<Note>();
00757                     adjusted.AddRange(touchEachSet);
00758                     adjusted.AddRange(eachSet);
00759                     eachSet = adjusted;
00760                 }
00761                 if (writeRest)
00762                 {
00763                     //Console.WriteLine("There is no note at tick " + i + " of bar " + barNumber + ", Adding one");
00764                     eachSet.Add(new Rest("RST", barNumber, i));
00765                 }
00766                 result.AddRange(eachSet);
00767             }
00768             if (RealNoteNumber(result) != RealNoteNumber(bar))
00769             {
00770                 string error = "";
00771                 error += ("Bar notes not match in bar: " + barNumber) + "\n";
00772                 error += ("Expected: " + RealNoteNumber(bar)) + "\n";
00773                 foreach (Note x in bar)
00774                 {
00775                     error += (x.Compose(1)) + "\n";
00776                 }
00777                 error += ("\nActual: " + RealNoteNumber(result)) + "\n";
00778                 foreach (Note y in result)
00779                 {
00780                     error += (y.Compose(1)) + "\n";
00781                 }
00782                 Console.WriteLine(error);
00783                 throw new Exception("NOTE NUMBER IS NOT MATCHING");
00784             }
00785             bool hasFirstBPMChange = false;
00786             List<Note> changedResult = new List<Note>();
00787             Note potentialFirstChange = new Rest();
00788             {
00789                 for (int i = 0; !hasFirstBPMChange && i < result.Count(); i++)
00790                 {
00791                     if (result[i].NoteGenre.Equals("BPM") && result[i].Tick == 0)
00792                     {
00793                         changedResult.Add(result[i]);
00794                         potentialFirstChange = result[i];
00795                         hasFirstBPMChange = true;
00796                     }
00797                 }
00798                 if (hasFirstBPMChange)
00799                 {
00800                     result.Remove(potentialFirstChange);
00801                     changedResult.AddRange(result);
00802                     result = changedResult;
00803                 }
00804             }
00805 
00806             return result;
00807         }
