00825         public static bool IsPrime(int number)
00826         {
00827             if (number < 1) return false;
00828             if (number == 2) return true;
00829             if (number % 2 == 0) return false;
00830 
00831             var boundary = (int)Math.Floor(Math.Sqrt(number));
00832 
00833             for (int i = 3; i <= boundary; i += 2)
00834                 if (number % i == 0)
00835                     return false;
00836 
00837             return true;
00838         }
