00861                     if (this.BPMChanges.ChangeNotes[i].TickStamp <= overallTick)
00862                     {
00863                         maximumBPMIndex = i;
00864                     }
00865                 }
00866                 if (maximumBPMIndex == 0)
00867                 {
00868                     result = 60 / this.BPMChanges.ChangeNotes[0].BPM * 4 / 384;
00869                 }
00870                 else
00871                 {
00872                     for (int i = 1; i <= maximumBPMIndex; i++)
00873                     {
00874                         double previousTickTimeUnit = 60 / this.BPMChanges.ChangeNotes[i - 1].BPM * 4 / 384;
00875                         result += (this.BPMChanges.ChangeNotes[i].TickStamp - this.BPMChanges.ChangeNotes[i - 1].TickStamp) * previousTickTimeUnit;
00876                     }
00877                     double tickTimeUnit = 60 / this.BPMChanges.ChangeNotes[maximumBPMIndex].BPM * 4 / 384;
00878                     result += (overallTick - this.BPMChanges.ChangeNotes[maximumBPMIndex].TickStamp) * tickTimeUnit;
00879                 }
00880             }
00881             return result;
00882         }
00883 
00889         public double GetTimeStamp(int overallTick)
00890         {
00891             double result = 0.0;
00892             if (overallTick != 0)
00893             {
00894                 bool foundMax = false;
00895                 int maximumBPMIndex = 0;
00896                 for (int i = 0; i < this.BPMChanges.ChangeNotes.Count && !foundMax; i++)
00897                 {
00898                     if (this.BPMChanges.ChangeNotes[i].TickStamp <= overallTick)
00899                     {
00900                         maximumBPMIndex = i;
00901                     }
00902                     else
00903                     {
00904                         foundMax = true;
00905                     }
00906                 }
00907                 if (maximumBPMIndex == 0)
00908                 {
00909                     result = GetBPMTimeUnit(this.BPMChanges.ChangeNotes[0].BPM) * overallTick;
00910                 }
00911                 else
00912                 {
00913                     for (int i = 1; i <= maximumBPMIndex; i++)
00914                     {
00915                         double previousTickTimeUnit = GetBPMTimeUnit(this.BPMChanges.ChangeNotes[i - 1].BPM);
00916                         result += (this.BPMChanges.ChangeNotes[i].TickStamp - this.BPMChanges.ChangeNotes[i - 1].TickStamp) * previousTickTimeUnit;
00917                     }
00918                     double tickTimeUnit = GetBPMTimeUnit(this.BPMChanges.ChangeNotes[maximumBPMIndex].BPM);
00919                     result += (overallTick - this.BPMChanges.ChangeNotes[maximumBPMIndex].TickStamp) * tickTimeUnit;
00920                 }
00921             }
00922             return result;
00923         }
00924 
00930         public static double GetTimeStamp(BPMChanges bpmChanges, int overallTick)
00931         {
00932             double result = 0.0;
00933             if (overallTick != 0)
00934             {
00935                 int maximumBPMIndex = 0;
00936                 for (int i = 0; i < bpmChanges.ChangeNotes.Count; i++)
00937                 {
00938                     if (bpmChanges.ChangeNotes[i].TickStamp <= overallTick)
00939                     {
00940                         maximumBPMIndex = i;
00941                     }
00942                 }
00943                 if (maximumBPMIndex == 0)
00944                 {
00945                     result = GetBPMTimeUnit(bpmChanges.ChangeNotes[0].BPM) * overallTick;
00946                 }
00947                 else
00948                 {
00949                     for (int i = 1; i <= maximumBPMIndex; i++)
00950                     {
00951                         double previousTickTimeUnit = GetBPMTimeUnit(bpmChanges.ChangeNotes[i - 1].BPM);
00952                         result += (bpmChanges.ChangeNotes[i].TickStamp - bpmChanges.ChangeNotes[i - 1].TickStamp) * previousTickTimeUnit;
00953                     }
00954                     double tickTimeUnit = GetBPMTimeUnit(bpmChanges.ChangeNotes[maximumBPMIndex].BPM);
00955                     result += (overallTick - bpmChanges.ChangeNotes[maximumBPMIndex].TickStamp) * tickTimeUnit;
00956                 }
00957             }
00958             return result;
00959         }
