00978         public static string GetNoteDetail(BPMChanges bpmChanges, Note inTake)
00979         {
00980             string result = "";
00981             result += inTake.Compose(1) + "\n";
00982             result += "This is a " + inTake.NoteSpecificGenre + " note,\n";
00983             result += "This note has overall tick of " + inTake.TickStamp + ", and therefor, the tick time stamp shall be " + GetTimeStamp(bpmChanges, inTake.TickStamp) + "\n";
00984             if (inTake.NoteGenre.Equals("SLIDE"))
00985             {
00986                 result += "This note has wait length of " + inTake.WaitLength + ", and therefor, its wait tick stamp is " + inTake.WaitTickStamp + " with wait time stamp of " + GetTimeStamp(bpmChanges, inTake.WaitTickStamp) + "\n";
00987                 result += "This note has last length of " + inTake.LastLength + ", and therefor, its last tick stamp is " + inTake.LastTickStamp + " with last time stamp of " + GetTimeStamp(bpmChanges, inTake.LastTickStamp) + "\n";
00988             }
00989             return result;
00990         }
