01022         public bool HasBPMChangeInBetween(int startTick, int endTick)
01023         {
01024             bool result = false;
01025 
01026             for (int i = 0; i < this.bpmChanges.ChangeNotes.Count && !result; i++)
01027             {
01028                 if (this.bpmChanges.ChangeNotes[i].TickStamp > startTick && this.bpmChanges.ChangeNotes[i].TickStamp < endTick)
01029                 {
01030                     result = true;
01031                 }
01032             }
01033 
01034             return result;
01035         }
