01037         public void ShiftByOffset(int overallTick)
01038         {
01039             List<Note> updatedNotes = new List<Note>();
01040             foreach (Note x in this.Notes)
01041             {
01042                 if (!x.NoteType.Equals("BPM") || !x.NoteGenre.Equals("MEASURE") || (x.NoteType.Equals("BPM") && x.Bar != 0 && x.Tick != 0) || (x.NoteGenre.Equals("MEASURE") && x.Bar != 0 && x.Tick != 0))
01043                 {
01044                     Note copy;
01045                     switch (x.NoteGenre)
01046                     {
01047                         case "TAP":
01048                         case "SLIDE_START":
01049                             copy = new Tap(x);
01050                             copy.Bar += overallTick / 384;
01051                             copy.Tick += overallTick % 384;
01052                             // if (copy.ConsecutiveSlide!=null)
01053                             // {
01054                             //     copy.ConsecutiveSlide.Bar += overallTick / 384;
01055                             //     copy.ConsecutiveSlide.Tick += overallTick % 384;
01056                             // }
01057                             copy.Update();
01058                             break;
01059                         case "HOLD":
01060                             copy = new Hold(x);
01061                             copy.Bar += overallTick / 384;
01062                             copy.Tick += overallTick % 384;
01063                             copy.Update();
01064                             break;
01065                         case "SLIDE":
01066                             copy = new Slide(x);
01067                             copy.Bar += overallTick / 384;
01068                             copy.Tick += overallTick % 384;
01069                             // if (copy.SlideStart != null)
01070                             // {
01071                             //     copy.SlideStart.Bar += overallTick / 384;
01072                             //     copy.SlideStart.Tick += overallTick % 384;
01073                             // }
01074                             // if (copy.ConsecutiveSlide != null)
01075                             // {
01076                             //     copy.ConsecutiveSlide.Bar += overallTick / 384;
01077                             //     copy.ConsecutiveSlide.Tick += overallTick % 384;
01078                             // }
01079                             copy.Update();
01080                             break;
01081                         case "BPM":
01082                             copy = new BPMChange(x);
01083                             copy.Bar += overallTick / 384;
01084                             copy.Tick += overallTick % 384;
01085                             copy.Update();
01086                             break;
01087                         case "MEASURE":
01088                             copy = new MeasureChange((MeasureChange)x);
01089                             copy.Bar += overallTick / 384;
01090                             copy.Tick += overallTick % 384;
01091                             copy.Update();
01092                             break;
01093                         default:
01094                             copy = new Rest();
01095                             break;
01096                     }
01097                     updatedNotes.Add(copy);
01098                 } 
01099                 else
01100                 {
01101                     updatedNotes.Add(x);
01102                 }
01103             }
01104             this.Notes = new List<Note>(updatedNotes);
01105             this.Update();
01106         }
01107 
01108         public void ShiftByOffset(int bar, int tick)
01109         {
01110             int overallTick = bar * 384 + tick;
01111             this.ShiftByOffset(overallTick);
01112         }
